import React from 'react';
import { useState,useEffect } from 'react';
import axios from 'axios';

function LandingPage() {
    const [data,setData]=useState([]);
    const [pageStart,setPageStart]=useState(0)
    const [pageEnd,setPageEnd]=useState(50)

    useEffect(()=>{
        const fetchData=async()=>{
            const response=await axios.get('https://api.coincap.io/v2/assets');
            setData(response.data.data)
        }
        fetchData();
    },[data]);

    const handlePagination=(e)=>{
        e.preventDefault();
        setPageEnd(pageEnd+50);
    }
 
    return (
        <>
              <div className="tabel">
                  <tr>
                      <th>Name</th>
                      <th>Price</th>
                      <th>Market Capital</th>
                      <th>Supply</th>
                      <th>Volume</th>
                  </tr>
                  {data?.slice(pageStart,pageEnd)?.map((value)=>{
                      return (
                          <tr>
                              <td>{value.name}</td>
                              <td>{value.priceUsd}</td>
                              <td>{value.marketCapUsd}</td>
                              <td>{value.maxSupply}</td>
                              <td>{value.volumeUsd24Hr}</td>
                          </tr>
                      )
                  })}
                  <button onClick={(e)=>handlePagination(e)}>Load More</button>
              </div>
       </>
    );
}

export default LandingPage;
